import React from 'react';
import BudgetItem from '../components/BudgetItem';

class Home extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            budgetedItems: [],
            itemValue: 0,
            itemName: ''
        }
        this.handleValueChange = this.handleValueChange.bind(this);
        this.handleNameChange = this.handleNameChange.bind(this);
    }

    addItem = (event) => {
        event.preventDefault();

        let itemArr = this.state.budgetedItems;
        itemArr.push({
            name: this.state.itemName,
            monthlyCost: this.state.itemValue
        })
    }
    handleValueChange(event) {
        this.setState({ itemValue: event.target.value });
    }

    handleNameChange(event) {
        this.setState({ itemName: event.target.value });
    }

    render() {
        return (
            <div>
                <h1>Next Budget</h1>

                <form onSubmit={this.addItem}>
                    <label>
                        Name:
                        <input type="text" value={this.state.itemName} onChange={this.handleNameChange} />
                    </label>
                    <br></br>
                    <label>
                        Value: $
                        <input type="number" value={this.state.itemValue} onChange={this.handleValueChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>

                <h1>Your monthly expenses:</h1>

            </div>
        )
    }
}

export default Home;
